sysauto_script.script:
// Get today's time and date
var past= new GlideDateTime();

// Query the database for NeedIt Task records with Due date field values within
// 24 hours from now.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var late= new GlideRecord('x_58872_needit_needit_task');
late.addQuery('due_date','<=',past);
late.addQuery('state','!=',3);

late.query();
// Write a log message for each overdue NeedIt Task Record
while(late.next()){
	gs.info("NeedIt Task due soon record = " + late.number);
gs.eventQueue('x_58872_needit.overdueNeedItTask',late,late.number,gs.getUserName());
}
